class BorderPaint{static pipe(...a){return function(b){return a.reduce((a,b)=>b(a),b)}}static getHue(){return Math.floor(101*Math.random())+190}static getDirectionInRadians(){return Math.random()*Math.PI}static getHypoLength(a){return 10/Math.cos(a)}static getCoord(a){const b=0>a?-1:1,c=Math.sqrt(a*a-100);return c*b}static getWidth(){return Math.floor(10*Math.random())+2}paint(a){for(let b=0;49>b;b+=1){const c=BorderPaint.getDirectionInRadians(),d=BorderPaint.pipe(BorderPaint.getHypoLength,BorderPaint.getCoord,Math.round)(c),e=6*b;a.beginPath(),a.moveTo(e,425),a.lineTo(e+d,435),a.lineWidth=BorderPaint.getWidth(),a.strokeStyle=`hsl(${BorderPaint.getHue()}deg, 85%, 49%)`,a.stroke()}}}registerPaint("borderPaint",BorderPaint);